package com.sdonkey.score.util;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;import org.apache.commons.lang.time.DateUtils;/** * 日期处理工具类. */public class DateUtil extends DateUtils {	/**	 * 缺省的日期格式	 */	public static final String DAFAULT_DATE_FORMAT = "yyyy-MM-dd";	/**	 * 时间样式 yyyy-MM-dd HH:mm:ss	 */	public static final String PARTTERN_DATE_TIME = "yyyy-MM-dd HH:mm:ss";	/**	 * 默认日期类型格试.	 * 	 */	private static SimpleDateFormat dateFormat = new SimpleDateFormat(			DAFAULT_DATE_FORMAT);	private DateUtil() {	}		public static String formatDate(Date d) {		if (d == null) {			return "";		}		return dateFormat.format(d);	}	public static Date getDateAddDay(int add) {		Calendar cal = Calendar.getInstance();		cal.add(Calendar.DATE, add);		return cal.getTime();	}}